					$this->view->health_insurance_ik = $healtharray['iknumber'];
				}

				if(strlen($healthinsu_array[0]['kvk_no']) == 0)
				{
					$this->view->health_insurance_kassenr = $healtharray['kvnumber'];
				}
			}

			if($hi_perms_divisions)
