    function check_lmu_pmba_psysoz_empty($lmu_pmba_vals)
    {
        //input fields for block
        $fam = array(
            "vater" => array(
                "is_array" => false,
                "fields" => array(
                    "vater_age" => array("type" => "text"),
                    "vater_note" => array("type" => "text"),
