					$conditions['ipids'] = $active_ipids;
				}
				else
				{
					$conditions['ipids'] = array("XXXXXX");
				}
			}
			$conditions['periods'] = $report_period_array;
			$conditions['client'] = $clientid;



			$sql = 'e.epid, p.ipid, e.ipid,';
