			{
				$check_assigned = "1";
				// get patients asigned to users

				$pats_users = Doctrine_Query::create()
					->select('*')
					->from('PatientQpaMapping')
					->where('userid = ' . $required_user)
					->andWhere(' clientid = ' . $clientid)
					->andWhere('epid!=""');
