			}

			if(count($ipidarr) == '0')
			{
				$ipidarr[] = '999999999';
			}

			//patients with valid sapv
			$ipidarr = array_values(array_unique($ipidarr));

			$patientsapv_vv = Doctrine_Query::create()
				->select("*")
