				->select("*")
				->from('SapvVerordnung sv')
				->whereIn('sv.ipid', $ipids)
				->andWhere('sv.status != 1')
				->andWhere('sv.isdelete = 0 and ((' . str_replace($s, $r, $active_cond['interval_sql']) . '))');
			$sapvarr = $patientsapv->fetchArray();

			foreach($sapvarr as $k_sapv => $v_sapv)
			{

