				$s_end = date('Y-m-d', strtotime($v_sapv['verordnungbis']));

				if(empty($all_sapv_days[$v_sapv['ipid']]))
				{
					$all_sapv_days[$v_sapv['ipid']] = array();
				}
				if(empty($temp_sapv_days[$v_sapv['ipid']]))
				{
					$temp_sapv_days[$v_sapv['ipid']] = array();
