			$bre_sapv = new BreSapvControl();
			$saved_bre_data = $bre_sapv->get_bre_sapv_controlsheet_report_period($ipids, $report_dates, $master_price_list, $active_days, $hospital_hospiz_days);

			/* ################################################################################################### */
