				}
				//print_r($patient_sapv);exit;
				//print_r($patient_final_sapv);exit;
				
				
				foreach($active_patients as $keypatient => $patient)
				{
				
					$MasterData['data'][$keypatient]['epid'] = $patient['EpidIpidMapping']['epid'] . ' ';
