			foreach($active_patient_details as $ipid => $p_details)
			{
				$patientDetails [$p_details['details'] ['ipid']] ['epid'] = $p_details['details'] ['epid'];
				$patientDetails [$p_details['details'] ['ipid']] ['surname'] = $p_details['details'] ['last_name'];
